  Monster monsterModel;

    
  public void setMonster( Monster m ) {
    monsterModel = m;
       
    nameLabel.setText( m.getName() );
    levelTextField.setText( String.valueOf( m.getCombatLevel() ) );
    levelCultistTextField.setText( String.valueOf( m.getCombatLevelAgainstCultistPlayer() ) );
    levelProgressBar.setValue( m.getCombatLevel() );
    prizeTextField.setText( m.getPrize().toString() );
    badConsequenceView.setBadConsequence( m.getBadConsequence() );
	String name = m.getName();
	monsterImageView.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Resources/" + name + ".png")));	
        
    repaint();
    revalidate();
  }
	public void setHiddenMonster( ) {
    nameLabel.setText( "多多??" );
    levelTextField.setText( "?" );
    levelCultistTextField.setText( "?" );
    levelProgressBar.setValue( 0 );
    prizeTextField.setText( "? tesoros, ? niveles." );
    badConsequenceView.setHiddenBadConsequence();
	monsterImageView.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Resources/No_Monster.png")));
	
    repaint();
    revalidate();
	}
